import logo from "./logo.svg";
import "./App.css";
import Notes from "./Components/Notes/Notes";
function App() {
  const [AddList, SetAddList] = useState([]);

  const AddListData = (Notes, Name, Date_no) => {
    SetAddList((prelist) => {
      return [
        ...prelist,
        {
          id: Math.random().toString(),
          notes: Notes,
          name: Name,
          date: Date_no,
        },
      ];
    });
  };

  return (
    <div className="App">
      <Notes adddetails={AddListData}></Notes>
    </div>
  );
}

export default App;
